import React from 'react'
import './Row.css'


function Row(props) {
        const category1=props.category;
        const movie1=props.movie;
        const FilteredMovie=movie1.filter((mov)=>
            mov['genres'].includes(category1)
        )  
        console.log(category1);
        console.log(FilteredMovie);
  return (
    <div className='rails'>
    {
      FilteredMovie.map((obj1)=>
      <div className='rail'>
        <img className='card' src={obj1['posterUrl'] } onError={(e)=>{e.target.src='https://static.vecteezy.com/system/resources/previews/005/337/799/original/icon-image-not-found-free-vector.jpg'}}></img>
        <h6>{obj1['title']}</h6>
      </div>)
    }
    </div>
  )
}

export default Row
